const Input = (
    {
        label = '',
        name = '',
        type = '',
        className='',
        isRequired = false,
        placeholder = ''
        },
) => {
  return (
    <div className='h'>
        <label for={name} className='block mb-2 text-sm font-medium text-gray-800'>{label}</label>
        <input type={type} id={name} className={`bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500
        focus:border-blue-500 block w-full  p-2.5 ${className}`} placeholder={placeholder} required={isRequired}/>
    </div>
  )
}

export default Input
